export const initailState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: 'KDONG',
      },
      content: '첫 번째 게시글 #크동 #프론트엔드',
      Images: [
        {
          src: 'https://scontent-ssn1-1.cdninstagram.com/v/t51.2885-15/sh0.08/e35/c0.180.1440.1440a/s640x640/254332861_931406257758483_2482629417920604977_n.jpg?_nc_ht=scontent-ssn1-1.cdninstagram.com&_nc_cat=103&_nc_ohc=N6SGn2OWkSkAX9fZJ3K&edm=ABfd0MgBAAAA&ccb=7-4&oh=00_AT844giJT7EqZsPKtQcj-Td86MHMHfVJ-iJ9bLTrfs3mWQ&oe=61FD0884&_nc_sid=7bff83',
