        {
          src: 'https://scontent-ssn1-1.cdninstagram.com/v/t51.2885-15/sh0.08/e35/c0.179.1440.1440a/s640x640/241733297_319927769904656_4130043869358235307_n.jpg?_nc_ht=scontent-ssn1-1.cdninstagram.com&_nc_cat=108&_nc_ohc=-r8pLUL6PlsAX-Ro5tb&edm=ABfd0MgBAAAA&ccb=7-4&oh=00_AT9wft9dHl6TzHLvNfCUTaDt4qKP2ZxXzoAB-d4kso_KRA&oe=61FE6949&_nc_sid=7bff83',
        },
      ],
      comments: [
        {
          User: {
            nickname: 'OMNA',
          },
          content: '대박 사진 멋있어요~~',
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: '더미데이터',
  User: {
    id: 1,
    nickname: 'KDONG',
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initailState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
      };
    default:
      return state;
  }
};

export default reducer;
