const initailState = {
  name: 'kdong',
  age: 30,
  password: 'omna',
};

// const changeNickname = {
//   type: 'CHANGE_NICKNAME',
//   data: 'KDONG',
// };

// aciton creator
const changeNickname = (data) => {
  return {
    type: 'CHANGE_NICKNAME',
    data,
  };
};
changeNickname('KDONG');
// {
//   type: 'CHANGE_NICKNAME',
//   data: 'KDONG'
// }

// reducer는 (이전상태, 액션) => 다음상태
const rootReducer = (state = initailState, action) => {
  switch (action.type) {
    case 'CHANGE_NICKNAME':
      return {
        ...state,
        name: action.data,
      };
  }
};
